mapper.select:
SELECT * FROM board3
subject LIKE CONCAT('%', #{search}, '%')
ORDER BY re_ref DESC, re_seq ASC LIMIT #{pageSize} OFFSET #{startRow}

mapper.update:
UPDATE board3 
SET readcount = readcount + 1 
WHERE num = #{num}

mapper.select:
SELECT COUNT(*) 
FROM board3 
WHERE num = #{num} 
AND passwd = #{passwd}

mapper.update:
UPDATE board3 
SET subject = #{subject}, content = #{content} 
WHERE num = #{num}

UPDATE board3
SET re_seq = re_seq + 1 
WHERE re_ref = #{reRef} 
AND re_seq > #{reSeq}
